CREATE TABLE "users" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "first_name" varchar(25) NOT NULL,
  "last_name" varchar(25) NOT NULL,
  "age" int,
  "role_fk" int,
  "email" varchar(50) UNIQUE NOT NULL,
  "password" varchar(25) NOT NULL
);

CREATE TABLE "courses" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "course_title" varchar(150),
  "category_fk" int,
  "description" text,
  "level" varchar(25),
  "teacher_fk" int
);

CREATE TABLE "course_modules" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "course_fk" int,
  "module_title" varchar(150),
  "module_description" text
);

CREATE TABLE "course_videos" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "module_fk" int,
  "title" varchar(150) NOT NULL,
  "url" text
);

CREATE TABLE "categories" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(70)
);

CREATE TABLE "roles" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(15)
);

CREATE TABLE "courses_by_user" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "user_fk" int,
  "course" int
);

ALTER TABLE "users" ADD FOREIGN KEY ("role_fk") REFERENCES "roles" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("teacher_fk") REFERENCES "users" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("category_fk") REFERENCES "categories" ("id");

ALTER TABLE "course_modules" ADD FOREIGN KEY ("course_fk") REFERENCES "courses" ("id");

ALTER TABLE "course_videos" ADD FOREIGN KEY ("module_fk") REFERENCES "course_modules" ("id");

ALTER TABLE "courses_by_user" ADD FOREIGN KEY ("user_fk") REFERENCES "users" ("id");

ALTER TABLE "courses_by_user" ADD FOREIGN KEY ("course") REFERENCES "courses" ("id");

